import base64

def decode_base64_pdf(encoded_string, output_file_path):
    try:
        # Decode the base64 string
        decoded_bytes = base64.b64decode(encoded_string)

        # Write the decoded content to a PDF file
        with open(output_file_path, 'wb') as output_file:
            output_file.write(decoded_bytes)

        print(f"Decoded PDF saved to {output_file_path}")

    except Exception as e:
        print(f"Error decoding base64 PDF: {str(e)}")

# Example usage:
encoded_pdf_string = "^"
output_pdf_file_path = "decoded_output-123--23.pdf"

decode_base64_pdf(encoded_pdf_string, output_pdf_file_path)
